import React, { useState } from "react";
import Lightbox from "../components/Lightbox";
import { galleryImages, AWARDS, REVIEWS } from "../data/gallery";

export default function Gallery() {
  const [open, setOpen] = useState(false);
  const [active, setActive] = useState(null);

  function openImg(img) {
    setActive(img);
    setOpen(true);
  }

  return (
    <div className="container">
      <div className="sectionHeader">
        <h2>Gallery</h2>
        <p>Interiors, dishes, and occasional chandelier-induced awe.</p>
      </div>

      <div className="card" style={{ padding: 16 }}>
        <div className="galleryGrid">
          {GALLERY.map((img) => (
            <img
              key={img.id}
              className="galleryImg"
              src={img.src}
              alt={img.title}
              onClick={() => openImg(img)}
            />
          ))}
        </div>
      </div>

      <section className="section grid2">
        <div className="card" style={{ padding: 16 }}>
          <h3 style={{ marginTop: 0 }}>Awards</h3>
          <ul className="help" style={{ marginTop: 0 }}>
            {AWARDS.map((a) => <li key={a}>{a}</li>)}
          </ul>
        </div>

        <div className="card" style={{ padding: 16 }}>
          <h3 style={{ marginTop: 0 }}>Reviews</h3>
          <div className="help" style={{ display: "grid", gap: 10 }}>
            {REVIEWS.map((r) => (
              <div key={r.who} className="alert">
                <b>{r.who}</b><br />{r.text}
              </div>
            ))}
          </div>
        </div>
      </section>

      <Lightbox
        open={open}
        image={active?.src}
        title={active?.title}
        onClose={() => setOpen(false)}
      />
    </div>
  );
}
