import React, { useEffect } from "react";

export default function Lightbox({ open, image, title, onClose }) {
  useEffect(() => {
    if (!open) return;
    const handler = (e) => {
      if (e.key === "Escape") onClose();
    };
    window.addEventListener("keydown", handler);
    return () => window.removeEventListener("keydown", handler);
  }, [open, onClose]);

  if (!open) return null;

  return (
    <div className="lightboxOverlay" onClick={onClose}>
      <div className="lightboxPanel" onClick={(e) => e.stopPropagation()}>
        <div className="lightboxTop">
          <div style={{ fontWeight: 800 }}>{title || "Gallery"}</div>
          <button className="btn" onClick={onClose}>Close</button>
        </div>
        <div className="lightboxBody">
          <img src={image} alt={title || "Gallery"} />
        </div>
      </div>
    </div>
  );
}
