import React, { useState } from "react";
import { postJson } from "../api";

export default function Footer() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [status, setStatus] = useState({ type: "", msg: "" });
  const [loading, setLoading] = useState(false);

  function isEmailValid(v) {
    return /^[^@\s]+@[^@\s]+\.[^@\s]+$/.test(v);
  }

  async function submit(e) {
    e.preventDefault();
    setStatus({ type: "", msg: "" });

    const f = firstName.trim();
    const l = lastName.trim();
    const v = email.trim().toLowerCase();

    if (!f) {
      setStatus({ type: "error", msg: "First name is required." });
      return;
    }
    if (!l) {
      setStatus({ type: "error", msg: "Last name is required." });
      return;
    }
    if (!isEmailValid(v)) {
      setStatus({ type: "error", msg: "Please enter a valid email." });
      return;
    }

    setLoading(true);
    const res = await postJson("/api/newsletter", { firstName: f, lastName: l, email: v });
    setLoading(false);

    if (res.ok && res.data?.ok) {
      setStatus({ type: "ok", msg: res.data.message || "Subscribed!" });
      setFirstName("");
      setLastName("");
      setEmail("");
    } else {
      setStatus({ type: "error", msg: res.data?.message || "Something went wrong." });
    }
  }

  return (
    <footer className="footer">
      <div className="footerInner">
        <div>
          <div className="brand" style={{ marginBottom: 6 }}>
            <span className="brandDot" />
            <span>Cafe Fausse</span>
          </div>
          <small>
            101 Rue Imaginaire, Paris • +33 1 23 45 67 89
            <br />
            Mon–Thu 5–10pm • Fri–Sat 5–11pm • Sun 11am–3pm
          </small>
        </div>

        <div className="card" style={{ padding: 14, minWidth: 320 }}>
          <div style={{ fontWeight: 800, marginBottom: 8 }}>Newsletter</div>
          <form onSubmit={submit} style={{ display: "grid", gap: 10 }}>
            <div className="row2">
              <input
                className="input"
                placeholder="First name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                required
              />
              <input
                className="input"
                placeholder="Last name"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                required
              />
            </div>

            <div style={{ display: "grid", gridTemplateColumns: "1fr auto", gap: 10 }}>
              <input
                className="input"
                placeholder="you@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <button className="btn btnGold" disabled={loading} type="submit">
                {loading ? "..." : "Join"}
              </button>
            </div>
          </form>

          {status.msg ? (
            <div className="alert" style={{ marginTop: 10 }}>
              {status.msg}
            </div>
          ) : (
            <div className="help" style={{ marginTop: 10 }}>
              Seasonal menus, chef’s table events, and zero spam (promise).
            </div>
          )}
        </div>
      </div>
    </footer>
  );
}
